mapper.update:
update setmeal


            id = #{id}


            , category_id = #{categoryId}


            , name = #{name}


            , price = #{price}


            , status = #{status}


            , description = #{description}


            , image = #{image}


            , update_time = #{updateTime}


            , update_user = #{updateUser}


where id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select s.id, s.name, s.category_id as categoryId, s.price, s.status, s.description, s.image, s.update_time as updateTime, c.name as categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%', #{name}, '%')
and s.category_id = #{categoryId}
and s.status = #{status}
order by s.create_time desc
